"""
Schema definitions and validation for Lineage data types.

All storage is JSON. No external dependencies.
Schemas are plain dicts validated by plain functions.
"""

MISSION_REQUIRED = {"id", "goal", "success_criteria", "constraints"}
PATTERN_REQUIRED = {"context", "problem", "solution"}
JOURNAL_REQUIRED = {"timestamp", "action", "rationale"}
ENTITY_REQUIRED = {"id", "type", "description"}

ENTITY_TYPES = {"component", "service", "library", "cluster", "person"}


def validate(data: dict, required: set, label: str) -> list[str]:
    """Return list of error strings. Empty list means valid."""
    errors = []
    missing = required - set(data.keys())
    if missing:
        errors.append(f"{label} missing required fields: {', '.join(sorted(missing))}")
    return errors


def validate_mission(data: dict) -> list[str]:
    return validate(data, MISSION_REQUIRED, "Mission")


def validate_pattern(data: dict) -> list[str]:
    return validate(data, PATTERN_REQUIRED, "Pattern")


def validate_journal(data: dict) -> list[str]:
    return validate(data, JOURNAL_REQUIRED, "Journal entry")


def validate_entity(data: dict) -> list[str]:
    errors = validate(data, ENTITY_REQUIRED, "Entity")
    if "type" in data and data["type"] not in ENTITY_TYPES:
        errors.append(f"Entity type must be one of: {', '.join(sorted(ENTITY_TYPES))}")
    return errors

"""
Lineage Store: filesystem CRUD for the memory backbone.

All reads and writes go through here. No direct file manipulation elsewhere.
JSON for documents, JSONL for append-only logs (journal, inbox).
"""

import json
import fcntl
from datetime import datetime, timezone
from pathlib import Path

from lineage_lib.schema import (
    validate_mission,
    validate_pattern,
    validate_journal,
    validate_entity,
)


def _lineage_root() -> Path:
    """Resolve the lineage data root. Walks up from this file to find it."""
    # lib/lineage_lib/store.py -> lib/ -> lineage/
    return Path(__file__).resolve().parent.parent.parent


def _resolve_root(root: Path | None = None) -> Path:
    return Path(root) if root else _lineage_root()


# ---------------------------------------------------------------------------
# Low-level I/O
# ---------------------------------------------------------------------------

def _read_json(path: Path) -> dict:
    """Read a single JSON file."""
    with open(path, "r") as f:
        return json.load(f)


def _write_json(path: Path, data: dict) -> None:
    """Write a single JSON file. Creates parent dirs if needed."""
    path.parent.mkdir(parents=True, exist_ok=True)
    with open(path, "w") as f:
        json.dump(data, f, indent=2, default=str)
        f.write("\n")


def _append_jsonl(path: Path, data: dict) -> None:
    """Append one JSON object to a JSONL file with file locking."""
    path.parent.mkdir(parents=True, exist_ok=True)
    with open(path, "a") as f:
        fcntl.flock(f, fcntl.LOCK_EX)
        try:
            f.write(json.dumps(data, default=str) + "\n")
        finally:
            fcntl.flock(f, fcntl.LOCK_UN)


def _read_jsonl(path: Path) -> list[dict]:
    """Read all lines from a JSONL file."""
    if not path.exists():
        return []
    entries = []
    with open(path, "r") as f:
        for line_num, line in enumerate(f, 1):
            line = line.strip()
            if line:
                try:
                    entries.append(json.loads(line))
                except json.JSONDecodeError:
                    entries.append({"_parse_error": True, "_line": line_num, "_raw": line})
    return entries


# ---------------------------------------------------------------------------
# Public API: Write
# ---------------------------------------------------------------------------

def write_journal(message: str, rationale: str, root: Path | None = None) -> dict:
    """Append an immutable journal entry."""
    root = _resolve_root(root)
    entry = {
        "timestamp": datetime.now(timezone.utc).isoformat(),
        "action": message,
        "rationale": rationale,
    }
    errors = validate_journal(entry)
    if errors:
        raise ValueError("; ".join(errors))
    _append_jsonl(root / "journal" / "journal.jsonl", entry)
    return entry


def write_intent(data: dict, root: Path | None = None) -> Path:
    """Write a mission file to intent/<id>.json."""
    root = _resolve_root(root)
    errors = validate_mission(data)
    if errors:
        raise ValueError("; ".join(errors))
    path = root / "intent" / f"{data['id']}.json"
    _write_json(path, data)
    return path


def write_pattern(data: dict, root: Path | None = None) -> Path:
    """Write a pattern file to patterns/<id>.json."""
    root = _resolve_root(root)
    if "id" not in data:
        raise ValueError("Pattern must have an 'id' field for its filename")
    errors = validate_pattern(data)
    if errors:
        raise ValueError("; ".join(errors))
    path = root / "patterns" / f"{data['id']}.json"
    _write_json(path, data)
    return path


def write_entity(data: dict, root: Path | None = None) -> Path:
    """Write an entity file to registry/entities/<id>.json."""
    root = _resolve_root(root)
    errors = validate_entity(data)
    if errors:
        raise ValueError("; ".join(errors))
    path = root / "registry" / "entities" / f"{data['id']}.json"
    _write_json(path, data)
    return path


def append_inbox(data: dict, root: Path | None = None) -> dict:
    """Append a raw item to the inbox staging area."""
    root = _resolve_root(root)
    if "timestamp" not in data:
        data["timestamp"] = datetime.now(timezone.utc).isoformat()
    _append_jsonl(root / "inbox" / "inbox.jsonl", data)
    return data


# ---------------------------------------------------------------------------
# Public API: Read
# ---------------------------------------------------------------------------

def read_intent(mission_id: str, root: Path | None = None) -> dict:
    """Read a single mission by ID."""
    root = _resolve_root(root)
    path = root / "intent" / f"{mission_id}.json"
    if not path.exists():
        raise FileNotFoundError(f"Mission not found: {mission_id}")
    return _read_json(path)


def read_pattern(pattern_id: str, root: Path | None = None) -> dict:
    """Read a single pattern by ID."""
    root = _resolve_root(root)
    path = root / "patterns" / f"{pattern_id}.json"
    if not path.exists():
        raise FileNotFoundError(f"Pattern not found: {pattern_id}")
    return _read_json(path)


def read_entity(entity_id: str, root: Path | None = None) -> dict:
    """Read a single entity by ID."""
    root = _resolve_root(root)
    path = root / "registry" / "entities" / f"{entity_id}.json"
    if not path.exists():
        raise FileNotFoundError(f"Entity not found: {entity_id}")
    return _read_json(path)


def read_journal(root: Path | None = None) -> list[dict]:
    """Read the full journal."""
    root = _resolve_root(root)
    return _read_jsonl(root / "journal" / "journal.jsonl")


def read_inbox(root: Path | None = None) -> list[dict]:
    """Read the inbox."""
    root = _resolve_root(root)
    return _read_jsonl(root / "inbox" / "inbox.jsonl")


# ---------------------------------------------------------------------------
# Public API: Search
# ---------------------------------------------------------------------------

def search(query: str, root: Path | None = None) -> list[dict]:
    """Brute-force text search across intent, patterns, and journal."""
    root = _resolve_root(root)
    query_lower = query.lower()
    results = []

    # Search intent files
    for path in (root / "intent").glob("*.json"):
        content = path.read_text()
        if query_lower in content.lower():
            results.append({"source": "intent", "file": path.name, "data": json.loads(content)})

    # Search pattern files
    for path in (root / "patterns").glob("*.json"):
        content = path.read_text()
        if query_lower in content.lower():
            results.append({"source": "pattern", "file": path.name, "data": json.loads(content)})

    # Search journal (line by line)
    journal_path = root / "journal" / "journal.jsonl"
    if journal_path.exists():
        for line in journal_path.read_text().splitlines():
            if line.strip() and query_lower in line.lower():
                results.append({"source": "journal", "file": "journal.jsonl", "data": json.loads(line)})

    # Search registry entities
    for path in (root / "registry" / "entities").glob("*.json"):
        content = path.read_text()
        if query_lower in content.lower():
            results.append({"source": "entity", "file": path.name, "data": json.loads(content)})

    return results


# ---------------------------------------------------------------------------
# Public API: List
# ---------------------------------------------------------------------------

def list_items(category: str, root: Path | None = None) -> list[str]:
    """List all item IDs in a category (intent, patterns, registry)."""
    root = _resolve_root(root)
    dir_map = {
        "intent": root / "intent",
        "patterns": root / "patterns",
        "entities": root / "registry" / "entities",
    }
    target = dir_map.get(category)
    if not target or not target.exists():
        return []
    return sorted(p.stem for p in target.glob("*.json"))

